SELECT schools.id, schools.name AS "school", courses.name AS "course", companies.name AS "company", roles.name AS "role" FROM educations JOIN courses ON educations."courseId"= courses.id JOIN schools ON educations."schoolId" = schools.id JOIN companies ON jobs."companyId"= companies.id JOIN jobs ON jobs."companyId" = companies.id JOIN roles ON jobs."roleId"= roles.id  WHERE companies.id = 10 AND jobs.active= true;